let btnOrder = [...document.querySelectorAll('.btn--mod')];

function openModalWindow() {
    if (btnOrder.length) {
        btnOrder.forEach((btn) => {
            btn.addEventListener('click', (e) => {
                e.preventDefault();
                e.stopPropagation();
                if (btn.classList.contains('main-mod')) {
                    document.querySelector('.modal-main').classList.add('active');

                } else {
                    document.querySelector('.modal-window').classList.add('active');
                }

                document.body.classList.add('no-scroll');
            })
        })
    }
}

openModalWindow();

function closeModals() {
    if (document.querySelector('.modal-window')) {
        let backplates = [...document.querySelectorAll('.backplate')];
        let closer = [...document.querySelectorAll('.modal-window .close')];
        backplates.forEach((btn) => {
            btn.addEventListener('click', () => {
                btn.closest('.modal-window').classList.remove('active');
                document.body.classList.remove('no-scroll');

            })
        });
        closer.forEach((btn) => {
            btn.addEventListener('click', () => {
                btn.closest('.modal-window').classList.remove('active');
                document.body.classList.remove('no-scroll');

            })
        })

    }
}

closeModals();


